import React, { useState } from 'react';
import '../styles/Modal.css';

const ReceiveModal = ({ network, onTest, onClose }) => {
  const [copied, setCopied] = useState(false);
  
  const handleCopyAddress = () => {
    if (network.addresses && network.addresses.length > 0) {
      navigator.clipboard.writeText(network.addresses[0]);
      setCopied(true);
      setTimeout(() => setCopied(false), 3000);
    }
  };
  
  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h3>{network.name} Al</h3>
          <button className="close-button" onClick={onClose}>&times;</button>
        </div>
        
        <div className="modal-content">
          <div className="receive-container">
            <div className="network-info-large">
              <div className="network-icon-large">
                <img src={network.iconUrl} alt={network.name} />
              </div>
              <div className="network-details">
                <span className="network-name-large">{network.name}</span>
                {network.networkIcon && (
                  <div className="network-type">
                    <img src={network.networkIcon} alt={network.network} className="network-icon-small" />
                    <span>{network.network} Ağı</span>
                  </div>
                )}
              </div>
            </div>
            
            {network.addresses && network.addresses.length > 0 ? (
              <>
                <div className="qr-code-container">
                  <div className="qr-code">
                    {/* QR kod yerine placeholder */}
                    <div className="qr-placeholder">
                      QR Kod
                    </div>
                  </div>
                </div>
                
                <div className="address-container-large">
                  <div className="address-label">Cüzdan Adresi</div>
                  <div className="address-value">{network.addresses[0]}</div>
                  <button 
                    className={`copy-button ${copied ? 'copied' : ''}`}
                    onClick={handleCopyAddress}
                  >
                    {copied ? 'Kopyalandı ✓' : 'Adresi Kopyala'}
                  </button>
                </div>
                
                <div className="receive-warning">
                  <div className="warning-icon">ℹ️</div>
                  <p>
                    Bu adrese yalnızca {network.name} ({network.symbol}) gönderin.
                    {network.networkIcon && ` ${network.network} ağı üzerinden gönderilmelidir.`}
                  </p>
                </div>
                
                <div className="test-button-container">
                  <button className="test-button" onClick={onTest}>
                    Test İşlemi Al
                  </button>
                  <p className="test-note">Bu buton yalnızca test amaçlıdır.</p>
                </div>
              </>
            ) : (
              <div className="no-address-container">
                <p>Bu ağ için henüz adres oluşturulmadı.</p>
                <p>Lütfen önce varlık ekranından adres oluşturun.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReceiveModal;
