import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/SideMenu.css';

const SideMenu = ({ 
  networks = [], 
  activeNetworkId, 
  totalBalance = 0, 
  onNetworkSelect, 
  onAddNewAccount 
}) => {
  const navigate = useNavigate();
  const [activeCategory, setActiveCategory] = useState('all');
  
  // Refresh CSS when component mounts
  useEffect(() => {
    if (window.electronAPI && window.electronAPI.refreshCSS) {
      console.log('SideMenu: CSS yenileme çağrılıyor...');
      setTimeout(() => {
        // Biraz gecikmeyle CSS yenileme yap, böylece component tam olarak yüklenmiş olur
        const result = window.electronAPI.refreshCSS();
        console.log('SideMenu: CSS yenileme sonucu:', result);
        
        // Manuel CSS uygulama - CSS'in doğrudan enjekte edilebilmesi için
        const forceCSSRefresh = () => {
          document.querySelectorAll('style').forEach(styleElem => {
            styleElem.textContent = styleElem.textContent + ' ';
          });
          // Gövde stillerini yeniden hesaplamak için
          document.body.style.display = 'none';
          setTimeout(() => document.body.style.display = '', 50);
        };
        
        // CSS'i zorla yenile
        forceCSSRefresh();
      }, 500);
    } else {
      console.warn('SideMenu: electronAPI veya refreshCSS fonksiyonu bulunamadı');
    }
  }, []);
  
  const handleLogout = () => {
    // Oturumu kapat ve giriş sayfasına yönlendir
    navigate('/');
  };
  
  // Ağ verileri boşsa default veri göster
  if (!networks || networks.length === 0) {
    return (
      <div className="side-menu">
        <div className="side-menu-header">
          <div className="wallet-balance">
            <h3>TOPLAM BAKİYE</h3>
            <div className="balance-value">
              <span>$0.00</span>
            </div>
          </div>
        </div>
        
        <div className="side-menu-content">
          <div className="wallet-categories">
            <div className="category-title">Kategoriler</div>
            <div className="category-list">
              <div className="category-pill active">Tümü</div>
              <div className="category-pill">Ana Coinler</div>
              <div className="category-pill">Tokenler</div>
              <div className="category-pill">Favoriler</div>
            </div>
          </div>
          
          <div className="network-list">
            <p className="empty-imported-msg">Yükleniyor...</p>
          </div>
        </div>
        
        <div className="side-menu-footer">
          <button className="btn-settings">
            <i className="fas fa-cog"></i>
            <span>Ayarlar</span>
          </button>
          
          <button className="btn-logout" onClick={handleLogout}>
            <i className="fas fa-sign-out-alt"></i>
            <span>Çıkış Yap</span>
          </button>
        </div>
      </div>
    );
  }
  
  // Cüzdanları ağ bazında grupla
  const groupedNetworks = networks.reduce((groups, network) => {
    // Artık sadece favorileri değil, tüm ağları göster
    const id = network.isToken ? network.network : network.id;
    
    if (!groups[id]) {
      const parentNetwork = networks.find(n => n.id === id);
      groups[id] = {
        id: id,
        name: parentNetwork ? parentNetwork.name : network.name,
        icon: parentNetwork ? parentNetwork.iconUrl : network.iconUrl,
        items: [],
        mainCoin: null  // Ana coin'i ayrıca saklayacağız
      };
    }
    
    // Token mı yoksa ana coin mi kontrol et
    if (network.isToken) {
      groups[id].items.push(network);
    } else {
      // Ana coin'i grubun başına koy
      groups[id].mainCoin = network;
    }
    
    return groups;
  }, {});
  
  // İçeri aktarılan cüzdanlar (demo için boş)
  const importedWallets = [];
  
  // Kategoriler arasında geçiş yap
  const handleCategoryChange = (category) => {
    setActiveCategory(category);
  };
  
  // Filtrelenmiş ağları göster
  const filteredNetworks = Object.values(groupedNetworks).filter(group => {
    if (activeCategory === 'all') return true;
    if (activeCategory === 'main' && group.mainCoin) return true;
    if (activeCategory === 'tokens' && group.items.length > 0) return true;
    if (activeCategory === 'favorites') {
      return group.mainCoin?.isFavorite || group.items.some(token => token.isFavorite);
    }
    return false;
  });
  
  return (
    <div className="side-menu">
      <div className="side-menu-header">
        <div className="wallet-balance">
          <h3>TOPLAM BAKİYE</h3>
          <div className="balance-value">
            <span>${totalBalance.toLocaleString('tr-TR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>
        </div>
      </div>
      
      <div className="side-menu-content">
        {/* Kategoriler */}
        <div className="wallet-categories">
          <div className="category-title">Kategoriler</div>
          <div className="category-list">
            <div 
              className={`category-pill ${activeCategory === 'all' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('all')}
            >
              Tümü
            </div>
            <div 
              className={`category-pill ${activeCategory === 'main' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('main')}
            >
              Ana Coinler
            </div>
            <div 
              className={`category-pill ${activeCategory === 'tokens' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('tokens')}
            >
              Tokenler
            </div>
            <div 
              className={`category-pill ${activeCategory === 'favorites' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('favorites')}
            >
              Favoriler
            </div>
          </div>
        </div>
        
        {/* Kripto Para Birimleri */}
        <div className="network-list">
          {filteredNetworks.map((group) => (
            <div key={group.id} className="network-group">
              <div className="network-group-header">
                <img src={group.icon} alt={group.name} className="network-icon" />
                <div className="network-group-name">{group.name} Ağı</div>
              </div>
              
              <div className="network-items">
                {/* Ana coin (eğer varsa ve kategori doğruysa) */}
                {group.mainCoin && (activeCategory === 'all' || activeCategory === 'main' || 
                  (activeCategory === 'favorites' && group.mainCoin.isFavorite)) && (
                  <div 
                    key={group.mainCoin.id}
                    className={`network-item ${group.mainCoin.id === activeNetworkId ? 'active' : ''}`}
                    onClick={() => onNetworkSelect(group.mainCoin.id)}
                  >
                    <div className="network-item-content">
                      <div className="network-icon">
                        <img src={group.mainCoin.iconUrl} alt={group.mainCoin.symbol} />
                      </div>
                      <div className="network-info">
                        <div className="network-name main-coin">
                          {group.mainCoin.symbol}
                        </div>
                        <div className="network-balance">
                          {parseFloat(group.mainCoin.balance).toFixed(8)} {group.mainCoin.symbol}
                        </div>
                      </div>
                    </div>
                    
                    <div className="network-value">
                      ${(group.mainCoin.balance * group.mainCoin.price).toFixed(2)}
                    </div>
                  </div>
                )}
                
                {/* Token'lar (eğer kategori doğruysa) */}
                {(activeCategory === 'all' || activeCategory === 'tokens' || activeCategory === 'favorites') && 
                  group.items
                    .filter(token => activeCategory !== 'favorites' || token.isFavorite)
                    .map(token => (
                      <div 
                        key={token.id}
                        className={`network-item token-item ${token.id === activeNetworkId ? 'active' : ''}`}
                        onClick={() => onNetworkSelect(token.id)}
                      >
                        <div className="network-item-content">
                          <div className="token-icon">
                            <img src={token.iconUrl} alt={token.name} />
                            <div className="parent-network-badge">
                              <img src={token.networkIcon || group.icon} alt={token.network} />
                            </div>
                          </div>
                          <div className="network-info">
                            <div className="network-name">
                              {token.symbol}
                            </div>
                            <div className="network-balance">
                              {parseFloat(token.balance).toFixed(token.decimals || 8)} {token.symbol}
                            </div>
                          </div>
                        </div>
                        
                        <div className="network-value">
                          ${(token.balance * token.price).toFixed(2)}
                        </div>
                      </div>
                    ))
                }
                
                {/* Yeni token ekleme butonu */}
                <button 
                  className="add-account-btn"
                  onClick={() => onAddNewAccount && onAddNewAccount(group.id)}
                >
                  <i className="fas fa-plus"></i>
                  <span>Token Ekle</span>
                </button>
              </div>
            </div>
          ))}
        </div>
        
        {/* İçeri Aktarılan Cüzdanlar */}
        <div className="imported-wallets-section">
          <div className="imported-wallets-header">
            <div className="imported-wallets-title">İÇERİ AKTARILAN CÜZDANLAR</div>
            <button className="import-wallet-btn">
              <i className="fas fa-file-import"></i>
              İçe Aktar
            </button>
          </div>
          
          {importedWallets.length === 0 ? (
            <div className="empty-imported-msg">Henüz cüzdan içeri aktarılmadı</div>
          ) : (
            <div className="imported-wallets-list">
              {/* İçeri aktarılan cüzdanlar burada gösterilecek */}
            </div>
          )}
        </div>
        
        {/* Yeni Cüzdan Oluşturma */}
        <button className="create-wallet-btn">
          <i className="fas fa-wallet"></i>
          Yeni Cüzdan Oluştur
        </button>
      </div>
      
      <div className="side-menu-footer">
        <button className="btn-settings">
          <i className="fas fa-cog"></i>
          <span>Ayarlar</span>
        </button>
        
        <button className="btn-logout" onClick={handleLogout}>
          <i className="fas fa-sign-out-alt"></i>
          <span>Çıkış Yap</span>
        </button>
      </div>
    </div>
  );
};

export default SideMenu;
