import React, { useState, useEffect } from 'react';
import '../styles/Modal.css';

const SettingsModal = ({ onClose }) => {
  const [activeTab, setActiveTab] = useState('general');
  const [confirmSeedPhrase, setConfirmSeedPhrase] = useState(false);
  const [showSeedPhrase, setShowSeedPhrase] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [activeWalletId, setActiveWalletId] = useState('');
  
  // Seed phrase (Gerçek projede cüzdandan gelecek)
  const seedPhrase = "valley alien library bread worry brother bundle hammer loyal barely dune brave";

  useEffect(() => {
    // localStorage'dan active wallet ID'yi al
    const storedWalletId = localStorage.getItem('activeWalletId');
    if (storedWalletId) {
      setActiveWalletId(storedWalletId);
    }
  }, []);

  const handleChangePassword = (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      alert('Şifreler eşleşmiyor.');
      return;
    }
    
    if (currentPassword === '') {
      alert('Mevcut şifrenizi girmelisiniz.');
      return;
    }
    
    // Şifre değiştirme işlemi (Gerçek projede API'ye gönderilecek)
    alert('Şifre başarıyla değiştirildi.');
    setCurrentPassword('');
    setNewPassword('');
    setConfirmPassword('');
  };

  const handleExportSeedPhrase = async () => {
    const password = prompt("Enter your wallet password to view your seed phrase");
    if (!password) return;
    
    setIsLoading(true);
    
    try {
      const result = await window.walletAPI.getSeedPhrase(activeWalletId, password);
      
      if (result.success) {
        // Create a plain text file with the mnemonic
        const content = `IMPORTANT: Keep this seed phrase secret and secure!\n\n${result.data}\n\nDate: ${new Date().toLocaleString()}\n\nThis seed phrase can be used to recover ALL your cryptocurrency wallets.`;
        
        // Save to a file
        const saveResult = await window.electronAPI.saveFile(
          content, 
          `cold-wallet-backup-${Date.now()}.txt`
        );
        
        if (saveResult.success) {
          alert(`Seed phrase has been saved successfully to: ${saveResult.filePath}`);
        } else {
          alert("Failed to save the seed phrase.");
        }
      } else {
        alert(`Error: ${result.error || 'Could not retrieve seed phrase'}`);
      }
    } catch (error) {
      console.error('Export error:', error);
      alert(`Error: ${error.message || 'An unexpected error occurred'}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExportPrivateKey = async () => {
    // Similar implementation for individual private keys
  };

  return (
    <div className="modal-overlay">
      <div className="modal settings-modal">
        <div className="modal-header">
          <h3>Ayarlar</h3>
          <button className="close-button" onClick={onClose}>×</button>
        </div>
        
        <div className="settings-tabs">
          <button 
            className={`tab-button ${activeTab === 'general' ? 'active' : ''}`}
            onClick={() => setActiveTab('general')}
          >
            Genel
          </button>
          <button 
            className={`tab-button ${activeTab === 'security' ? 'active' : ''}`}
            onClick={() => setActiveTab('security')}
          >
            Güvenlik
          </button>
          <button 
            className={`tab-button ${activeTab === 'backup' ? 'active' : ''}`}
            onClick={() => setActiveTab('backup')}
          >
            Yedekleme
          </button>
          <button 
            className={`tab-button ${activeTab === 'about' ? 'active' : ''}`}
            onClick={() => setActiveTab('about')}
          >
            Hakkında
          </button>
        </div>
        
        <div className="settings-content">
          {activeTab === 'general' && (
            <div className="settings-section">
              <h4>Genel Ayarlar</h4>
              
              <div className="form-group">
                <label>Dil</label>
                <select defaultValue="tr">
                  <option value="tr">Türkçe</option>
                  <option value="en">English</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Para Birimi</label>
                <select defaultValue="usd">
                  <option value="usd">USD</option>
                  <option value="eur">EUR</option>
                  <option value="try">TRY</option>
                </select>
              </div>
              
              <div className="form-group checkbox">
                <input type="checkbox" id="auto-lock" defaultChecked />
                <label htmlFor="auto-lock">5 dakika sonra otomatik kilitle</label>
              </div>
              
              <div className="form-group checkbox">
                <input type="checkbox" id="notifications" defaultChecked />
                <label htmlFor="notifications">İşlem bildirimlerini göster</label>
              </div>
              
              <button className="primary-button">Kaydet</button>
            </div>
          )}
          
          {activeTab === 'security' && (
            <div className="settings-section">
              <h4>Güvenlik Ayarları</h4>
              
              <form onSubmit={handleChangePassword}>
                <div className="form-group">
                  <label>Mevcut Şifre</label>
                  <input 
                    type="password" 
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                  />
                </div>
                
                <div className="form-group">
                  <label>Yeni Şifre</label>
                  <input 
                    type="password" 
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                </div>
                
                <div className="form-group">
                  <label>Yeni Şifre (Tekrar)</label>
                  <input 
                    type="password" 
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                  />
                </div>
                
                <button type="submit" className="primary-button">Şifreyi Değiştir</button>
              </form>
              
              <hr />
              
              <div className="form-group checkbox">
                <input type="checkbox" id="biometric" />
                <label htmlFor="biometric">Biyometrik kimlik doğrulamayı etkinleştir</label>
              </div>
              
              <div className="form-group checkbox">
                <input type="checkbox" id="2fa" />
                <label htmlFor="2fa">İki faktörlü kimlik doğrulama (2FA)</label>
              </div>
            </div>
          )}
          
          {activeTab === 'backup' && (
            <div className="settings-section">
              <h4>Cüzdan Yedekleme</h4>
              
              <div className="warning-box">
                <p>
                  <strong>Uyarı:</strong> 12 kelimelik kurtarma ifadeniz, cüzdanınıza erişiminizin tek yoludur. 
                  Bu ifadeyi güvenli bir yerde saklayın ve kimseyle paylaşmayın.
                </p>
              </div>
              
              <div className="form-group checkbox">
                <input 
                  type="checkbox" 
                  id="confirm-backup" 
                  checked={confirmSeedPhrase}
                  onChange={(e) => setConfirmSeedPhrase(e.target.checked)}
                />
                <label htmlFor="confirm-backup">
                  Kurtarma ifademi göstermenin risklerini anlıyorum ve gizliliğini koruyacağım.
                </label>
              </div>
              
              {confirmSeedPhrase && (
                <div className="seed-phrase-container">
                  {showSeedPhrase ? (
                    <div className="seed-words">
                      {seedPhrase.split(' ').map((word, index) => (
                        <div key={index} className="seed-word">
                          <span className="word-number">{index + 1}</span>
                          <span className="word">{word}</span>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="blur-box" onClick={() => setShowSeedPhrase(true)}>
                      Kurtarma ifadesini görmek için tıklayın
                    </div>
                  )}
                  
                  {showSeedPhrase && (
                    <button 
                      className="copy-button"
                      onClick={() => {
                        navigator.clipboard.writeText(seedPhrase);
                        alert('Kurtarma ifadesi kopyalandı!');
                      }}
                    >
                      Kopyala
                    </button>
                  )}
                </div>
              )}
              
              <hr />
              
              <button className="secondary-button" onClick={handleExportSeedPhrase}>Cüzdanı Dışa Aktar</button>
            </div>
          )}
          
          {activeTab === 'about' && (
            <div className="settings-section about">
              <div className="logo-container">
                <img src={require('../assets/logo.png')} alt="EG Logo" />
                <h3>Exchange Global</h3>
                <p>Soğuk Cüzdan</p>
              </div>
              
              <div className="version-info">
                <p>Sürüm: 1.0.0</p>
                <p>Yapım Tarihi: 2024</p>
              </div>
              
              <div className="about-text">
                <p>
                  Exchange Global Soğuk Cüzdan, kripto varlıklarınızı güvenle yönetmeniz 
                  için tasarlanmış çevrimdışı bir cüzdan uygulamasıdır.
                </p>
                <p>
                  © 2024 Exchange Global. Tüm hakları saklıdır.
                </p>
              </div>
              
              <button className="text-button">Güncellemeleri Kontrol Et</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SettingsModal;
