import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import TitleBar from './TitleBar';
import '../styles/LoginScreen.css';

const LoginScreen = () => {
  const navigate = useNavigate();
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (error) setError('');
  };

  const handleLogin = async (e) => {
    if (e) e.preventDefault();
    
    console.log('Login işlemi başlatılıyor...');
    
    if (!password) {
      setError('Lütfen şifre girin');
      return;
    }
    
    setIsSubmitting(true);
    setError('');
    
    try {
      console.log('Şifre doğrulama yapılıyor...');
      
      // Elektron API'sini kontrol et
      if (window.walletAPI) {
        // Elektron API kullanarak şifre doğrulama
        const isValid = await window.walletAPI.verifyPassword(password);
        if (isValid) {
          console.log('Şifre Electron API ile doğrulandı');
          loginSuccess();
          return;
        } else {
          setError('Hatalı şifre!');
        }
      } else {
        // Test için doğrudan sabit şifre kontrolü
        const correctPassword = 'd$QI*^1%wiqGg2*v6XY5';
        
        // Şifre kontrolü - basit eşleştirme
        if (password === correctPassword) {
          console.log('Şifre doğru, loginSuccess fonksiyonu çağırılıyor...');
          loginSuccess();
          return;
        } else {
          console.log('Şifre kontrolü başarısız!');
          setError('Hatalı şifre!');
        }
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Giriş işlemi sırasında bir hata oluştu: ' + err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Başarılı giriş sonrası işlemler
  const loginSuccess = () => {
    console.log('Başarılı giriş, localStorage güncelleniyor...');
    
    // localStorage kaydını güncelle
    localStorage.setItem('activeWalletId', 'wallet-1');
    localStorage.setItem('sessionToken', 'auth-token-' + Date.now());
    
    // localStorage'in güncellendiğini kontrol et
    console.log('activeWalletId:', localStorage.getItem('activeWalletId'));
    console.log('sessionToken:', localStorage.getItem('sessionToken'));
    
    // Dashboard'a yönlendir
    console.log('Dashboard sayfasına yönlendiriliyor...');
    
    // Daha uzun bir gecikme ekleyerek localStorage'in güncellendiğinden emin olalım
    // ve React'ın yönlendirmeyi yapabilmesi için yeterli süre sağlayalım
    setTimeout(() => {
      navigate('/dashboard');
      console.log('Navigate çağrısı yapıldı');
    }, 500);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  useEffect(() => {
    const inputElement = document.getElementById("password-input");
    if (inputElement) inputElement.focus();
    
    // localStorage durumunu kontrol et
    console.log('Mevcut localStorage durumu:');
    console.log('activeWalletId:', localStorage.getItem('activeWalletId'));
    console.log('sessionToken:', localStorage.getItem('sessionToken'));
  }, []);

  // Enter tuşuna basınca giriş yap
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      console.log('Enter tuşuna basıldı, giriş yapılıyor...');
      handleLogin(e);
    }
  };

  // Function to handle actions from the UI
  const handleAction = (action) => {
    console.log(`Action triggered: ${action}`);
    // Additional action handlers could be implemented here
  };

  return (
    <div className="login-root">
      {/* Use the shared TitleBar component */}
      <TitleBar />

      {/* Giriş Kartı - Yeni Tasarım */}
      <div className="login-container">
        <div className="wallet-logo">
          <div className="logo-container">
            <img src={require('../assets/logo.png')} alt="Exchange Global Logo" className="eg-logo" />
          </div>
        </div>

        {/* Exchange Global Yazısı - Unlock to Continue stilinde */}
        <h1 className="unlock-title exchange-global-title">Exchange Global</h1>

        <h1 className="unlock-title">Unlock to Continue</h1>
        <p className="wallet-status">Your wallet is secured</p>

        <form className="password-container" onSubmit={handleLogin}>
          <div className="password-input-wrapper">
            <div className="password-icon" onClick={togglePasswordVisibility}>
              <svg viewBox="0 0 24 24" className="eye-icon">
                <path d="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z" />
              </svg>
            </div>
            <input
              type={showPassword ? "text" : "password"}
              id="password-input"
              className="modern-input"
              placeholder="Type your password..."
              value={password}
              onChange={handlePasswordChange}
              autoComplete="off"
              onKeyPress={handleKeyPress}
              disabled={isSubmitting}
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button 
            className="login-button"
            type="submit"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Giriş Yapılıyor...' : 'Giriş Yap'}
          </button>
        </form>

        <div className="action-buttons">
          <button className="action-button help-button" onClick={() => handleAction('help')}>
            <svg viewBox="0 0 24 24" className="action-icon">
              <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 17h-2v-2h2v2zm2.07-7.75l-.9.92c-.5.51-.86.97-1.04 1.69-.08.32-.13.68-.13 1.14h-2v-.5c0-.46.08-.9.22-1.31.2-.58.53-1.1.95-1.52l1.24-1.26c.46-.44.68-1.1.55-1.8-.13-.72-.69-1.33-1.39-1.53-1.11-.31-2.14.32-2.47 1.27-.12.37-.43.65-.82.65h-.3C8.4 9 8 8.44 8.16 7.88c.43-1.47 1.68-2.59 3.23-2.83 1.52-.24 2.97.55 3.87 1.8 1.18 1.63.83 3.38-.19 4.4z" />
            </svg>
            <span>Help</span>
          </button>
          
          <button className="action-button restore-button" onClick={() => handleAction('restore')}>
            <svg viewBox="0 0 24 24" className="action-icon">
              <path d="M13 3c-4.97 0-9 4.03-9 9H1l4 4 4-4H6c0-3.86 3.14-7 7-7s7 3.14 7 7-3.14 7-7 7c-1.9 0-3.62-.76-4.88-1.99L6.7 18.42C8.32 20.01 10.55 21 13 21c4.97 0 9-4.03 9-9s-4.03-9-9-9zm-1 5v5l4.25 2.52.77-1.28-3.52-2.09V8z" />
            </svg>
            <span>Restore</span>
          </button>
          
          <button 
            className="action-button quit-button" 
            onClick={() => window.electronAPI && window.electronAPI.closeWindow()}
          >
            <svg viewBox="0 0 24 24" className="action-icon">
              <path d="M13 3h-2v10h2V3zm4.83 2.17l-1.42 1.42C17.99 7.86 19 9.81 19 12c0 3.87-3.13 7-7 7s-7-3.13-7-7c0-2.19 1.01-4.14 2.58-5.42L6.17 5.17C4.23 6.82 3 9.26 3 12c0 4.97 4.03 9 9 9s9-4.03 9-9c0-2.74-1.23-5.18-3.17-6.83z" />
            </svg>
            <span>Quit</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginScreen;
