import React, { useState, useEffect } from 'react';
import '../../styles/Dashboard.css';

const AssetDetail = ({ 
  asset = null, 
  onSend, 
  onReceive, 
  onGenerateAddress, 
  isGeneratingAddress = false,
  transactions = [],
  activeNetwork,
  transactionFee
}) => {
  const [activeTab, setActiveTab] = useState('activity');
  const [copySuccess, setCopySuccess] = useState(false);
  
  // Refresh CSS when component mounts
  useEffect(() => {
    if (window.electronAPI && window.electronAPI.refreshCSS) {
      console.log('AssetDetail: CSS yenileme çağrılıyor...');
      setTimeout(() => {
        // Biraz gecikmeyle CSS yenileme yap, böylece component tam olarak yüklenmiş olur
        const result = window.electronAPI.refreshCSS();
        console.log('AssetDetail: CSS yenileme sonucu:', result);
        
        // Manuel CSS uygulama - CSS'in doğrudan enjekte edilebilmesi için
        const forceCSSRefresh = () => {
          document.querySelectorAll('style').forEach(styleElem => {
            styleElem.textContent = styleElem.textContent + ' ';
          });
          // Gövde stillerini yeniden hesaplamak için
          document.body.style.display = 'none';
          setTimeout(() => document.body.style.display = '', 50);
        };
        
        // CSS'i zorla yenile
        forceCSSRefresh();
      }, 500);
    } else {
      console.warn('AssetDetail: electronAPI veya refreshCSS fonksiyonu bulunamadı');
    }
  }, []);
  
  // Kopyalama işlevini yönet
  const handleCopyAddress = (address) => {
    navigator.clipboard.writeText(address);
    setCopySuccess(true);
    setTimeout(() => setCopySuccess(false), 2000);
  };
  
  // Veri kontrolü
  if (!asset) {
    return (
      <div className="asset-detail-container">
        <div className="empty-state">
          <div className="empty-icon">🔍</div>
          <p>Lütfen sol taraftan bir varlık seçin</p>
        </div>
      </div>
    );
  }
  
  // Varsayılan değerler atama (null veya undefined için)
  const assetData = {
    name: asset.name || 'Bilinmeyen Varlık',
    symbol: asset.symbol || '???',
    iconUrl: asset.iconUrl || require('../../assets/default-coin.png'),
    networkIcon: asset.networkIcon,
    network: asset.network || 'Bilinmeyen Ağ',
    balance: asset.balance || 0,
    price: asset.price || 0,
    change24h: asset.change24h || 0,
    addresses: asset.addresses || []
  };
  
  const formatDate = (dateStr, timeStr) => {
    return `${dateStr} ${timeStr || ''}`;
  };

  const openExplorer = (address) => {
    let explorerUrl = '';
    
    // Ağa göre explorer URL'i belirle
    if (asset.id === 'bitcoin') {
      explorerUrl = `https://blockstream.info/address/${address}`;
    } else if (asset.id === 'ethereum' || asset.network === 'ETH') {
      explorerUrl = `https://etherscan.io/address/${address}`;
    } else if (asset.id === 'tron' || asset.network === 'TRX') {
      explorerUrl = `https://tronscan.org/#/address/${address}`;
    } else if (asset.id === 'binance' || asset.network === 'BSC') {
      explorerUrl = `https://bscscan.com/address/${address}`;
    } else if (asset.id === 'solana') {
      explorerUrl = `https://explorer.solana.com/address/${address}`;
    } else {
      alert('Bu ağ için explorer desteği bulunmamaktadır.');
      return;
    }
    
    // Yeni pencerede aç
    window.open(explorerUrl, '_blank');
  };
  
  const renderTransactions = () => {
    if (!transactions || transactions.length === 0) {
      return (
        <div className="empty-state">
          <div className="empty-icon">📊</div>
          <p>Henüz işlem geçmişi bulunmuyor</p>
        </div>
      );
    }
    
    return (
      <div className="transaction-list">
        {transactions.map(tx => (
          <div className="transaction-item" key={tx.id}>
            <div className={`transaction-icon ${tx.type === 'send' ? 'send-icon' : 'receive-icon'}`}>
              {tx.type === 'send' ? '↑' : '↓'}
            </div>
            
            <div className="transaction-details">
              <div className="transaction-header">
                <span className="transaction-type">
                  {tx.type === 'send' ? 'Gönderildi' : 'Alındı'}
                </span>
                <span className="transaction-date">
                  {formatDate(tx.date, tx.time)}
                </span>
              </div>
              
              <div className="transaction-info">
                <span className="transaction-address">
                  {tx.to || 'Kendi Adresiniz'}
                </span>
                <span className={`transaction-amount ${tx.type === 'send' ? 'negative' : 'positive'}`}>
                  {tx.type === 'send' ? '-' : '+'}{tx.amount} {tx.currency}
                </span>
              </div>
              
              <div className="transaction-status">
                <span className={`status-badge status-${tx.status}`}>
                  {tx.status === 'completed' ? 'Tamamlandı' : 
                   tx.status === 'pending' ? 'İşlemde' : 'Başarısız'}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };
  
  return (
    <div className="asset-detail-container">
      {/* Varlık başlığı */}
      <div className="asset-header">
        <div className="asset-title">
          <div className="asset-icon-large">
            <img src={assetData.iconUrl} alt={assetData.name} />
          </div>
          <div className="asset-name-container">
            <h2>{assetData.name} <span className="asset-symbol">{assetData.symbol}</span></h2>
            <div className="asset-network">
              {assetData.networkIcon && (
                <>
                  <img src={assetData.networkIcon} alt={assetData.network} className="detail-network-icon" />
                  <span>{assetData.network} Ağı</span>
                </>
              )}
            </div>
          </div>
        </div>
        
        <div className="asset-balance-container">
          <div className="primary-balance">
            <span className="token-balance">{assetData.balance.toFixed(8)}</span>
            <span className="token-symbol">{assetData.symbol}</span>
          </div>
          <div className="fiat-balance">
            ≈ ${(assetData.balance * assetData.price).toFixed(2)} USD
          </div>
          <div className="price-info">
            <div className="current-price-info">
              <span className="price-label">Anlık Fiyat</span>
              <span className="price-value">${assetData.price.toFixed(2)}</span>
            </div>
            <div className={`price-change-info ${assetData.change24h >= 0 ? 'positive' : 'negative'}`}>
              {assetData.change24h >= 0 ? '▲' : '▼'}
              {Math.abs(assetData.change24h).toFixed(2)}%
            </div>
          </div>
        </div>
      </div>
      
      {/* İşlem butonları */}
      <div className="asset-actions">
        <button className="action-button send" onClick={onSend}>
          <span className="action-icon">↑</span>
          <span>Gönder</span>
        </button>
        <button className="action-button receive" onClick={onReceive}>
          <span className="action-icon">↓</span>
          <span>Al</span>
        </button>
        <button className="action-button swap">
          <span className="action-icon">⇄</span>
          <span>Takas</span>
        </button>
      </div>
      
      {/* Adres bölümü */}
      <div className="address-section">
        <div className="section-header">
          <h3>Cüzdan Adresi</h3>
        </div>
        
        {assetData.addresses && assetData.addresses.length > 0 ? (
          <div className="address-container">
            <div className="address-display">
              {assetData.addresses[0]}
            </div>
            <div className="address-actions">
              <button 
                className="address-button"
                onClick={() => handleCopyAddress(assetData.addresses[0])}
              >
                <span className="button-icon">{copySuccess ? '✓' : '📋'}</span>
                <span>{copySuccess ? 'Kopyalandı!' : 'Kopyala'}</span>
              </button>
              <button 
                className="address-button"
                onClick={() => openExplorer(assetData.addresses[0])}
              >
                <span className="button-icon">🔍</span>
                <span>Explorer'da Görüntüle</span>
              </button>
            </div>
          </div>
        ) : (
          <div className="no-address-container">
            <p>Henüz bu kripto para için bir adres oluşturulmadı</p>
            <button 
              className="generate-button"
              onClick={onGenerateAddress}
              disabled={isGeneratingAddress}
            >
              {isGeneratingAddress ? 'Oluşturuluyor...' : 'Adres Oluştur'}
            </button>
          </div>
        )}
      </div>
      
      {/* İşlem geçmişi sekmeleri */}
      <div className="detail-tabs">
        <div className="tab-headers">
          <button 
            className={`tab-button ${activeTab === 'activity' ? 'active' : ''}`}
            onClick={() => setActiveTab('activity')}
          >
            İşlem Geçmişi
          </button>
          <button 
            className={`tab-button ${activeTab === 'about' ? 'active' : ''}`}
            onClick={() => setActiveTab('about')}
          >
            Hakkında
          </button>
        </div>
        
        <div className="tab-content">
          {activeTab === 'activity' && (
            <div className="activity-content">
              {renderTransactions()}
            </div>
          )}
          
          {activeTab === 'about' && (
            <div className="about-content">
              <h3>{assetData.name} ({assetData.symbol})</h3>
              <p className="about-description">
                {asset.description || `${assetData.name}, merkeziyetsiz bir kripto para birimidir. Bu sayfada coin hakkında daha fazla bilgi görebileceksiniz. Şu anda geliştirme aşamasındadır.`}
              </p>
              <div className="about-links">
                <a href="#" className="about-link">Resmi Web Sitesi</a>
                <a href="#" className="about-link">Explorer</a>
                <a href="#" className="about-link">CoinGecko</a>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AssetDetail;
