import React, { useState, useEffect } from 'react';
import '../styles/SendModal.css';

const SendModal = ({ network, onSend, onClose }) => {
  const [amount, setAmount] = useState('');
  const [to, setTo] = useState('');
  const [memo, setMemo] = useState('');
  const [addressValid, setAddressValid] = useState(true);
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [fee, setFee] = useState(null);
  const [feeCurrency, setFeeCurrency] = useState(network.symbol);
  const [total, setTotal] = useState(0);
  
  // Adres doğrulama
  useEffect(() => {
    if (!to || to.length < 5) {
      setAddressValid(false);
      return;
    }
    
    const validateAddress = async () => {
      // walletAPI var mı kontrol et
      if (!window.walletAPI) {
        // Test modunda her zaman geçerli kabul et
        setAddressValid(true);
        return;
      }
      
      try {
        const networkCode = network.isToken ? network.network : network.id;
        const response = await window.walletAPI.validateAddress(networkCode, to);
        setAddressValid(response.success && response.valid);
      } catch (error) {
        console.error('Address validation error:', error);
        setAddressValid(false);
      }
    };
    
    // 500ms debounce ile doğrula
    const timer = setTimeout(() => {
      validateAddress();
    }, 500);
    
    return () => clearTimeout(timer);
  }, [to, network]);
  
  // İşlem ücreti tahmini
  useEffect(() => {
    if (!to || !amount || !addressValid || isNaN(parseFloat(amount))) {
      return;
    }
    
    const estimateFee = async () => {
      // walletAPI var mı kontrol et
      if (!window.walletAPI) {
        // Test modunda standart ücret
        const defaultFees = {
          'bitcoin': 0.0001,
          'ethereum': 0.002,
          'tron': 5,
          'binance': 0.0005,
          'solana': 0.000005
        };
        
        const estimatedFee = defaultFees[network.id] || 0.001;
        setFee(estimatedFee);
        setFeeCurrency(network.symbol);
        
        // Toplam miktarı hesapla
        if (network.isToken) {
          // Token için ana coin ücreti ayrı
          setTotal(parseFloat(amount));
        } else {
          // Ana coin için ücret dahil toplam
          setTotal(parseFloat(amount) + estimatedFee);
        }
        
        return;
      }
      
      try {
        const activeWalletId = localStorage.getItem('activeWalletId');
        if (!activeWalletId) {
          throw new Error("Aktif cüzdan ID'si bulunamadı");
        }
        
        const from = network.addresses[0];
        const networkCode = network.isToken ? network.network : network.id;
        
        const response = await window.walletAPI.estimateFee(
          activeWalletId,
          networkCode,
          from,
          to,
          amount
        );
        
        if (response.success) {
          setFee(response.fee);
          setFeeCurrency(response.fee_currency);
          
          // Toplam miktarı hesapla
          if (network.isToken) {
            // Token için ana coin ücreti ayrı
            setTotal(parseFloat(amount));
          } else {
            // Ana coin için ücret dahil toplam
            setTotal(parseFloat(amount) + response.fee);
          }
        }
      } catch (error) {
        console.error('Fee estimation error:', error);
      }
    };
    
    // 500ms debounce ile ücret tahmini
    const timer = setTimeout(() => {
      estimateFee();
    }, 500);
    
    return () => clearTimeout(timer);
  }, [to, amount, addressValid, network]);
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Temel doğrulama
    if (!to) {
      setError('Lütfen bir adres girin');
      return;
    }
    
    if (!addressValid) {
      setError('Lütfen geçerli bir adres girin');
      return;
    }
    
    if (!amount || isNaN(parseFloat(amount)) || parseFloat(amount) <= 0) {
      setError('Lütfen geçerli bir miktar girin');
      return;
    }
    
    // Bakiye kontrolü
    if (parseFloat(amount) > network.balance) {
      setError('Yetersiz bakiye');
      return;
    }
    
    setIsSubmitting(true);
    setError('');
    
    try {
      await onSend(to, parseFloat(amount), memo);
    } catch (error) {
      console.error('Send error:', error);
      setError(error.message || 'İşlem gönderilirken bir hata oluştu');
      setIsSubmitting(false);
    }
  };
  
  return (
    <div className="modal-backdrop">
      <div className="modal-container send-modal">
        <div className="modal-header">
          <h2>
            <img src={network.iconUrl} alt={network.name} className="currency-icon" />
            {network.name} Gönder
          </h2>
          <button className="close-button" onClick={onClose} disabled={isSubmitting}>
            <i className="fas fa-times"></i>
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="modal-content">
            {error && (
              <div className="error-message">
                <p>{error}</p>
              </div>
            )}
            
            <div className="form-group">
              <label htmlFor="to-address">Alıcı Adresi</label>
              <input
                type="text"
                id="to-address"
                value={to}
                onChange={(e) => setTo(e.target.value)}
                placeholder={`${network.name} adresi girin`}
                className={!addressValid && to ? 'invalid' : ''}
                disabled={isSubmitting}
              />
              {!addressValid && to && (
                <p className="field-error">Geçersiz adres</p>
              )}
            </div>
            
            <div className="form-group">
              <div className="amount-header">
                <label htmlFor="amount">Gönderilecek Miktar</label>
                <div className="available-balance">
                  <span>Kullanılabilir: </span>
                  <span className="balance-value">{network.balance} {network.symbol}</span>
                </div>
              </div>
              
              <div className="amount-input-container">
                <input
                  type="text"
                  id="amount"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  placeholder="0.00"
                  disabled={isSubmitting}
                />
                <div className="amount-suffix">
                  <span className="currency-symbol">{network.symbol}</span>
                  <button
                    type="button"
                    className="max-button"
                    onClick={() => setAmount(network.balance.toString())}
                    disabled={isSubmitting}
                  >
                    MAX
                  </button>
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="memo">Açıklama (Opsiyonel)</label>
              <input
                type="text"
                id="memo"
                value={memo}
                onChange={(e) => setMemo(e.target.value)}
                placeholder="İşlem için açıklama ekleyin"
                disabled={isSubmitting}
              />
            </div>
            
            {fee !== null && (
              <div className="fee-container">
                <div className="fee-row">
                  <span className="fee-label">İşlem Ücreti:</span>
                  <span className="fee-value">{fee} {feeCurrency}</span>
                </div>
                
                {!network.isToken && (
                  <div className="fee-row total">
                    <span className="fee-label">Toplam:</span>
                    <span className="fee-value">{total.toFixed(8)} {network.symbol}</span>
                  </div>
                )}
                
                {network.isToken && (
                  <div className="token-fee-note">
                    <i className="fas fa-info-circle"></i>
                    <span>
                      Bu token işlemi için {fee} {feeCurrency} işlem ücreti, {network.network} 
                      ağ bakiyenizden düşülecektir.
                    </span>
                  </div>
                )}
              </div>
            )}
          </div>
          
          <div className="modal-footer">
            <button
              type="button"
              className="btn-secondary"
              onClick={onClose}
              disabled={isSubmitting}
            >
              İptal
            </button>
            
            <button
              type="submit"
              className="btn-primary"
              disabled={isSubmitting || !addressValid || !amount || parseFloat(amount) <= 0}
            >
              {isSubmitting ? (
                <>
                  <div className="spinner-small"></div>
                  <span>İşlem Gönderiliyor...</span>
                </>
              ) : (
                <>
                  <i className="fas fa-paper-plane"></i>
                  Gönder
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SendModal;
